<script type="text/javascript">
  var offlineId = "TL-HYD-MO-GEN-0488";
  var mspid, titleValue, qtyValue, priceValue, mobileValue, orderId;

  setCookieMins("3hrspopup", "1", 15);
  setCookieMins("autoPopup", "1", 15);

  function validateOfflineForm() {
    var $form = $(".popup-inner-content"),
        $submitButton = $("#popup-submit");

    if ($form.hasClass("step1")) {
      titleValue = $(".product-name div").text();
      
      qtyValue = parseInt($("#popup-qty").val(), 10);
      if (isNaN(qtyValue) || qtyValue < 1 || qtyValue > 99) {
        alert("Please enter a valid quantity.");
        $("#popup-qty").focus();
        return false;
      }
      
      var mobileRegex = /^\d{10}$/;
      mobileValue = $.trim($("#popup-mobile").val());
      if (!mobileRegex.test(mobileValue)) {
        alert("Please enter a valid 10-digit mobile number.");
        $("#popup-mobile").focus();
        return false;
      }

      var $pageTitle = $("#mspSingleTitle");
      mspid = $pageTitle.data("mspid");
      priceValue = $(".price-value span").text();

      $submitButton.addClass("btn-disabled");
      $.ajax({
        "url": "/offline/offline_delivery.php",
        "dataType": "json",
        "data": {
          "firststep": 1,
          "mspid": mspid,
          "offlineid": offlineId,
          "title": titleValue,
          "quantity": qtyValue,
          "price": priceValue,
          "mobile": mobileValue
        }
      }).done(function (json) {
        if (json && json.stores && json.stores.length) {
          var store = json.stores[0];
          if (store.orderid) {
            orderId = store.orderid;
            $(".step-done-card .headline span").text(orderId);
            if (store.storephone) {
              $(".store-phone").text(store.storephone);
              $(".store-phone-outer").show();
            }
            if (store.shipping) {
              if (store.shipping.pickup)
                $("#popup-pickup").prop("disabled", false).click();
              if (store.shipping.delivery)
                $("#popup-delivery").prop("disabled", false).click();
              if (store.shipping.pickup || store.shipping.delivery)
                $("#popup-name, #popup-email").show();
            }
            $form.toggleClass("step1 step2");
            $submitButton.val($submitButton.data("steptwo"));
          }
        }
      }).always(function () {
        $submitButton.removeClass("btn-disabled");
      });
    }
    else if ($form.hasClass("step2")) {
      if (!$("#popup-pickup").is(":checked") && !$("#popup-delivery").is(":checked")) {
        alert("Please select a shipping option.");
        $("#popup-pickup").focus();
        return false;
      }
      var shippingValue = $.trim($(".shipping-option:checked").val());

      var nameValue = $.trim($("#popup-name").val());
      if (!nameValue.length) {
        alert("Please enter your name.");
        $("#popup-name").focus();
        return false;
      }

      var emailRegex = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/,
          emailValue = $.trim($("#popup-email").val());
      if (!emailRegex.test(emailValue)) {
        alert("Please enter a valid email.");
        $("#popup-email").focus();
        return false;
      }

      var request = {
        "firststep": 0,
        "mspid": mspid,
        "offlineid": offlineId,
        "title": titleValue,
        "quantity": qtyValue,
        "price": priceValue,
        "mobile": mobileValue,
        "shipping": shippingValue,
        "name": nameValue,
        "email": emailValue,
        "orderid": orderId
      };

      if ($("#popup-delivery").is(":checked")) {
        var addressValue = $.trim($("#popup-address").val());
        if (!addressValue.length) {
          alert("Please enter your address.");
          $("#popup-address").focus();
          return false;
        }
        request.address = addressValue + ' ' + $.trim($("#popup-landmark").val());

        var pincodeRegex = /^\d{6}$/,
            pincodeValue = $.trim($("#popup-pincode").val());
        if (!pincodeRegex.test(pincodeValue)) {
          alert("Please enter your pin code.");
          $("#popup-pincode").focus();
          return false;
        }
        request.pincode = pincodeValue;
      }

      $.ajax({
        "url": "/offline/offline_delivery.php",
        "data": request
      });

      $form.toggleClass("step2 step-done");
    }
    return false;
  }

  $(document).ready(function () {
    setTimeout(function() {
      $('#popup-mobile').focus();
    }, 600);

    var $store = $(".prc-tbl-row").filter(".js-ofln__" + offlineId),
        $pageTitle = $(".prdct-dtl__ttl");
    
    $(".product-image img").attr({
      "src": $(".prdct-dtl__img").attr("src"),
      "alt": $pageTitle.text()
    });

    var size = $(".prdct-dtl__ttl-vrnt").data("size");
    size = size ? " (" + size + ")" : "";
    $(".product-name div").text($pageTitle.text() + size);
    $(".price-value span").text($store.data("pricerank"));

    var $storeImage = $store.find(".prc-tbl__str-logo");
    $(".product-seller img, .store-logo img").attr({
      "src": $storeImage.attr("src"),
      "alt": $storeImage.attr("alt")
    });
    $(".store-name").text($storeImage.attr("alt"));
    $(".product-seller .address, .store-address").text($store.data("address") + ", " + $store.find(".store_area").text());
  });

  $(document).off("click.offlineDeliveryPopup", "#popup-back").on("click.offlineDeliveryPopup", "#popup-back", function () {
    var $submitButton = $("#popup-submit");
    $submitButton.val($submitButton.data("stepone"));
    $(".popup-inner-content").toggleClass("step2 step1");
  });

  $(document).off("change.offlineDeliveryPopup", ".shipping-option").on("change.offlineDeliveryPopup", ".shipping-option", function () {
    if ($("#popup-pickup").is(":checked")) {
      $(".payment-box, #popup-address, #popup-landmark, #popup-city, #popup-pincode").hide();
      $(".note-box, #pickup-note").show();
    }
    else if ($("#popup-delivery").is(":checked")) {
      $(".note-box, #pickup-note").hide();
      $(".payment-box, #popup-address, #popup-landmark, #popup-city, #popup-pincode").show();
    }
    setTimeout("$('#popup-name').focus();", 200);
  });
</script>
